CREATE OR REPLACE PROCEDURE medreceita_nexodata (nr_sequencia_p bigint) AS $body$
BEGIN
  if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
     update med_receita set ds_externo = 'NEXODATA' where nr_sequencia = nr_sequencia_p
     and exists (SELECT 	1
		from 	credenciais_integracao
		where 	ie_sistema = 'NEXODATAREC'
		and	(ds_endereco IS NOT NULL AND ds_endereco::text <> '')
		and 	(ds_login IS NOT NULL AND ds_login::text <> '')
		and 	(ds_senha IS NOT NULL AND ds_senha::text <> ''));
     commit;
  end if;
end;
