CREATE OR REPLACE PROCEDURE med_agenda_consulta_afterpost ( cd_pessoa_fisica_p text, cd_medico_p text, cd_convenio_p bigint, nr_seq_plano_p bigint, cd_agenda_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_cliente_w	bigint;


BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then 
	begin	 
	/* Busca a sequencia do cliente */
 
	select	coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_cliente_w 
	from	med_cliente 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	cd_medico	= cd_medico_p;
	 
	if (nr_seq_cliente_w <> 0) then 
		begin		 
		CALL atualizar_convenio_med_cliente(nr_seq_cliente_w, cd_convenio_p,	'', 'N', nr_seq_plano_p);		
		 
		CALL gravar_paciente_agenda_setor(cd_agenda_p, cd_pessoa_fisica_p, nm_usuario_p);
		 
		CALL gerar_autor_regra( 
			null, 
			null, 
			null, 
			null, 
			null, 
			null, 
			'AC', 
			nm_usuario_p, 
			null, 
			null, 
			null, 
			nr_sequencia_p, 
			null, 
			null, 
			null, 
			null, 
			null);
		end;
	end if;	
	end;
end if;
 
commit;
 
end;
$body$
