SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_alterar_data ( nr_sequencia_p bigint, dt_data_p timestamp, ie_opcao_p text) AS $body$
DECLARE


/* ie_opcao_p
E = Evoluções
R = Receitas
S = Solicitação de exames
T = textos adicionais
A = Atestados
C = Confidências
D - Diagnósticos
*/
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (dt_data_p IS NOT NULL AND dt_data_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	if (ie_opcao_p = 'E') then

		update	med_evolucao
		set	   	dt_evolucao		= dt_data_p
		where	nr_sequencia	= nr_sequencia_p;

	elsif (ie_opcao_p = 'R') then

		update	med_receita
		set	   	dt_receita		= dt_data_p
		where	nr_sequencia	= nr_sequencia_p;

	elsif (ie_opcao_p = 'S') then

		update	med_pedido_exame
		set	   	dt_solicitacao	= dt_data_p
		where	nr_sequencia	= nr_sequencia_p;

	elsif (ie_opcao_p = 'T') then

		update	med_texto_adicional
		set	   	dt_texto		= dt_data_p
		where	nr_sequencia	= nr_sequencia_p;

	elsif (ie_opcao_p = 'A') then

		update	med_atestado
		set	   	dt_atestado		= dt_data_p
		where	nr_sequencia	= nr_sequencia_p;

	elsif (ie_opcao_p = 'C') then

		update	med_confidencia
		set	dt_confidencia	= dt_data_p
		where	nr_sequencia	= nr_sequencia_p;

	elsif (ie_opcao_p = 'D') then

		update	med_pac_diagnostico
		set	dt_diagnostico	= dt_data_p
		where	nr_sequencia	= nr_sequencia_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
