CREATE OR REPLACE PROCEDURE med_alt_result_exame_afterpost ( nr_seq_cliente_p bigint, dt_exame_p timestamp, nr_seq_exame_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_ficha_p INOUT text) AS $body$
DECLARE


vl_parametro_w		varchar(255);
cd_pessoa_sist_orig_w	varchar(10);
qt_existe_w		bigint;


BEGIN

if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') and (dt_exame_p IS NOT NULL AND dt_exame_p::text <> '') and (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then
	begin
	CALL med_atualiza_resultado_exame(nr_seq_cliente_p, dt_exame_p, nr_seq_exame_p,'N');

	vl_parametro_w := Obter_Param_Usuario(9007, 23, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);

	if (vl_parametro_w = 'S') then
		begin
		CALL Med_Gerar_Ficha_Paciente(nr_seq_cliente_p, nm_usuario_p);

		select	count(*)
		into STRICT	qt_existe_w
		from	med_cliente
		where	nr_sequencia = nr_seq_cliente_p;

		if (qt_existe_w > 0) then
			select	cd_pessoa_sist_orig
			into STRICT	cd_pessoa_sist_orig_w
			from	med_cliente
			where	nr_sequencia = nr_seq_cliente_p;
		end if;
		end;
	end if;

	CALL med_ultima_atualizacao_cliente(nr_seq_cliente_p, '', '', 'C');
	end;
end if;

nr_ficha_p	:= cd_pessoa_sist_orig_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
