CREATE OR REPLACE PROCEDURE med_atendimento_afterpost ( nr_seq_cliente_p bigint, cd_convenio_p bigint, cd_usuario_convenio_p text, nr_seq_plano_p bigint, ds_plano_p INOUT text, ds_convenio_p INOUT text) AS $body$
DECLARE


ds_plano_w	varchar(40)	:= '';
ds_convenio_w	varchar(255)	:= '';


BEGIN

if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	begin

	/* Atualiza convênio médico */

	CALL atualizar_convenio_med_cliente(nr_seq_cliente_p, cd_convenio_p, cd_usuario_convenio_p, 'S', nr_seq_plano_p);

	if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') then
		select	ds_plano
		into STRICT	ds_plano_w
		from	med_plano
		where	nr_sequencia = nr_seq_plano_p;
	end if;

	if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then
		select	ds_convenio
		into STRICT	ds_convenio_w
		from	convenio
		where	cd_convenio = cd_convenio_p;
	end if;

	/* Atualiza a data da última atualização na tabela med_cliente */

	CALL Med_Ultima_Atualizacao_Cliente(nr_seq_cliente_p, '', '', 'C');

	end;
end if;

ds_plano_p	:= ds_plano_w;
ds_convenio_p	:= ds_convenio_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
