CREATE OR REPLACE PROCEDURE med_atualizar_nome_paciente ( nr_seq_agenda_p bigint) AS $body$
DECLARE

 
nm_pessoa_fisica_w	varchar(100);


BEGIN 
 
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then 
	begin	 
 
	select	substr(obter_nome_pessoa_fisica(cd_pessoa_fisica, null),1,100) 
	into STRICT	nm_pessoa_fisica_w 
	from	agenda_consulta 
	where	nr_sequencia	= nr_seq_agenda_p;
	 
	update	agenda_consulta 
	set	nm_paciente	= nm_pessoa_fisica_w 
	where	nr_sequencia	= nr_seq_agenda_p;
	 
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
