CREATE OR REPLACE PROCEDURE med_atualiza_seguranca (cd_pessoa_fisica_p text, cd_medico_p text, nm_tabela_p text, nm_usuario_p text, nr_seq_cliente_p bigint, cd_pessoa_atual_p INOUT text) AS $body$
DECLARE



nr_sequencia_w		bigint;
cd_pessoa_atual_w		varchar(10) := '0';


BEGIN

CALL consiste_med_seguranca(nm_usuario_p);

delete	from med_seguranca
where	cd_pessoa_fisica 	= cd_pessoa_fisica_p;


if (length(nm_tabela_p) > 0) then
	begin


	select	coalesce(max(cd_pessoa_fisica),'0')
	into STRICT	cd_pessoa_atual_w
	from	med_seguranca
	where	nm_tabela		= nm_tabela_p
	and	cd_medico		= cd_medico_p
	and	nr_seq_cliente		= nr_seq_cliente_p;


	if (cd_pessoa_atual_w	= '0') and (nr_seq_cliente_p	> 0) then
		begin

		select	nextval('med_seguranca_seq')
		into STRICT	nr_sequencia_w
		;

		insert	into med_seguranca(nr_sequencia,
			cd_pessoa_fisica,
			cd_medico,
			nm_tabela,
			nr_seq_cliente,
			dt_atualizacao,
			nm_usuario)
		values (nr_sequencia_w,
			cd_pessoa_fisica_p,
			cd_medico_p,
			nm_tabela_p,
			nr_seq_cliente_p,
			clock_timestamp(),
			nm_usuario_p);
		end;
	end if;
	end;

end if;


if (cd_pessoa_atual_w = cd_medico_p) then
	cd_pessoa_atual_p	:= '0';
else
	cd_pessoa_atual_p	:= cd_pessoa_atual_w;
end if;
commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
