CREATE OR REPLACE PROCEDURE med_bloquear_agenda_dia ( cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w	bigint;
									
c01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	agenda_consulta 
	where	trunc(dt_agenda,'dd')	= dt_agenda_p 
	and	cd_agenda		= cd_agenda_p 
	and	ie_status_agenda		= 'L';
									

BEGIN 
 
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then 
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		CALL alterar_status_agecons(cd_agenda_p, nr_sequencia_w, 'B', null, null, 'N', nm_usuario_p, 0);
		 
		end;
	end loop;
	close C01;
	 
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
