SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_copiar_agenda_lista_espera (nr_seq_agenda_p bigint, ie_cancelar_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_agenda_w		bigint;
cd_medico_w		varchar(10);
nr_ficha_w		varchar(10);
cd_pessoa_fisica_w	varchar(10);
cd_convenio_w		bigint;
nr_telefone_w		varchar(80);
nr_seq_plano_w		bigint;
nr_seq_lista_w		bigint;
nm_paciente_w		varchar(80);


BEGIN 
 
select	coalesce(max(a.cd_agenda),0), 
	max(a.cd_pessoa_fisica), 
	max(b.cd_pessoa_fisica), 
	max(b.cd_convenio), 
	max(b.nr_seq_plano), 
	max(b.nr_telefone), 
	max(b.nm_paciente) 
into STRICT	cd_agenda_w, 
	cd_medico_w, 
	cd_pessoa_fisica_w, 
	cd_convenio_w, 
	nr_seq_plano_w, 
	nr_telefone_w, 
	nm_paciente_w 
from	agenda a, 
	agenda_consulta b 
where	a.cd_agenda	= b.cd_agenda 
and	b.nr_sequencia	= nr_seq_agenda_p;
 
 
if (cd_agenda_w > 0) then 
	begin 
 
	select	max(cd_pessoa_sist_orig) 
	into STRICT	nr_ficha_w 
	from	med_cliente 
	where	cd_medico	= cd_medico_w 
	and	cd_pessoa_fisica	= cd_pessoa_fisica_w;	
 
	select	nextval('med_lista_espera_seq') 
	into STRICT	nr_seq_lista_w		 
	;
 
	insert	into med_lista_espera( 
		NR_SEQUENCIA      , 
		CD_AGENDA       , 
		DT_ATUALIZACAO     , 
		NM_USUARIO       , 
		NR_SEQ_PRIORIDADE   , 
		NR_FICHA        , 
		CD_PESSOA_FISICA    , 
		nm_pessoa_lista		, 
		DT_CONTATO       , 
		CD_CONVENIO      , 
		NR_TELEFONE      , 
		NR_SEQ_PLANO      ) 
	values (nr_seq_lista_w		, 
		cd_agenda_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		100, 
		nr_ficha_w, 
		cd_pessoa_fisica_w, 
		nm_paciente_w, 
		clock_timestamp(), 
		cd_convenio_w, 
		substr(nr_telefone_w,1,15), 
		nr_seq_plano_w);
 
	if (ie_cancelar_p = 'S') then		 
		update	agenda_consulta 
		set	ie_status_agenda	= 'C' 
		where	nr_sequencia	= nr_seq_agenda_p;
	end if;
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
