SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_copiar_avaliacao (nr_seq_origem_p bigint, nr_seq_destino_p bigint, nm_usuario_p text) AS $body$
BEGIN

insert 	into Med_Pac_Cid(nr_sequencia,
	nr_seq_cliente,
	cd_doenca_cid,
	dt_atualizacao,
	nm_usuario,
	ds_observacao,
	dt_diagnostico,
	nr_seq_avaliacao,
	ie_severidade,
	ie_primario,
	ie_classificacao,
	ie_status)
(SELECT	nextval('med_pac_cid_seq'),
	nr_seq_cliente,
	cd_doenca_cid,
	clock_timestamp(),
	nm_usuario_p,
	ds_observacao,
	dt_diagnostico,
	nr_Seq_destino_p,
	ie_severidade,
	ie_primario,
	ie_classificacao,
	ie_status
from	Med_Pac_Cid a
where	a.nr_seq_avaliacao	= nr_seq_origem_p
and	not exists (select	1
	from	Med_Pac_Cid x
	where	x.cd_doenca_cid		= a.cd_doenca_cid
	and	x.nr_seq_avaliacao	= nr_Seq_destino_p));

insert 	into Med_Medic_Paciente(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	nr_seq_medic,
	qt_dose,
	cd_unid_med,
	cd_intervalo,
	ie_via,
	nr_Seq_aval_compl,
	ie_situacao,
	ie_intervalo,
	ie_prescrito,
	qt_medicamento)
(SELECT	nextval('med_medic_paciente_seq'),
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_medic,
	qt_dose,
	cd_unid_med,
	cd_intervalo,
	ie_via,
	nr_Seq_destino_p,
	ie_situacao,
	ie_intervalo,
	ie_prescrito,
	qt_medicamento
from	Med_Medic_Paciente a
where	a.nr_seq_aval_compl	= nr_seq_origem_p
and	not exists (select	1
	from	Med_Medic_Paciente x
	where	x.nr_seq_medic		= a.nr_seq_medic
	and	x.nr_Seq_aval_compl	= nr_Seq_destino_p));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
