CREATE OR REPLACE PROCEDURE med_copiar_faturamento (cd_medico_origem_p text, cd_medico_destino_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_origem_w		bigint;
nr_seq_plano_w		bigint;
cd_convenio_w		integer;
ds_plano_w		varchar(40);
cd_interface_w		integer;
ie_situacao_w		varchar(01);
nr_seq_item_orig_w	bigint;
nr_seq_item_w		bigint;
ds_item_w		varchar(80);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_convenio,
		ds_plano,
		cd_interface,
		ie_situacao
	from	med_plano
	where	cd_medico	= cd_medico_origem_p;

c02 CURSOR FOR
	SELECT	nr_sequencia,
		ds_item
	from	med_item
	where	cd_medico	= cd_medico_origem_p;


BEGIN

open	c01;
loop
fetch	c01 into
	nr_seq_origem_w,
	cd_convenio_w,
	ds_plano_w,
	cd_interface_w,
	ie_situacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('med_plano_seq')
	into STRICT	nr_seq_plano_w
	;

	insert	into med_plano(nr_sequencia,
		cd_convenio,
		ds_plano,
		cd_medico,
		dt_atualizacao,
		nm_usuario,
		cd_interface,
		ie_situacao)
	values (nr_seq_plano_w,
		cd_convenio_w,
		ds_plano_w,
		cd_medico_destino_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_interface_w,
		ie_situacao_w);


	open	c02;
	loop
	fetch	c02 into
		nr_seq_item_orig_w,
		ds_item_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_item_w
		from	med_item
		where	cd_medico	= cd_medico_destino_p
		and	ds_item		= ds_item_w;

		if (nr_seq_item_w	= 0) then
			begin

			select	nextval('med_item_seq')
			into STRICT	nr_seq_item_w
			;

			insert	into med_item(nr_sequencia,
				cd_medico,
				dt_atualizacao,
				nm_usuario,
				ds_item)
			values (nr_seq_item_w,
				cd_medico_destino_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_item_w);

			end;
		end if;

		insert	into med_item_convenio(nr_sequencia,
			nr_seq_plano,
			nr_seq_item,
			dt_atualizacao,
			nm_usuario,
			cd_item_convenio,
			vl_padrao,
			ie_tipo_guia)
		(SELECT	nextval('med_item_convenio_seq'),
			nr_seq_plano_w,
			nr_seq_item_w,
			clock_timestamp(),
			nm_usuario_p,
			cd_item_convenio,
			vl_padrao,
			ie_tipo_guia
		from	med_item_convenio
		where	nr_seq_plano	= nr_seq_origem_w
		and	nr_seq_item	= nr_seq_item_orig_w);



		end;
	end loop;
	close	c02;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
