CREATE OR REPLACE PROCEDURE med_escalas_indices_newrecord ( nr_seq_cliente_p bigint, nr_atendimento_p INOUT bigint, qt_idade_p INOUT bigint, qt_altura_cm_p INOUT bigint) AS $body$
DECLARE

 
nr_atendimento_w	bigint := 0;
qt_idade_w		integer := 0;
qt_altura_cm_w		double precision := 0;
		

BEGIN 
 
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then 
	begin 
	 
	select	coalesce(max(nr_atendimento),0) 
	into STRICT	nr_atendimento_w 
	from	med_atendimento 
	where	nr_seq_cliente	= nr_seq_cliente_p;
	 
	select	obter_idade_pf(cd_pessoa_fisica, clock_timestamp(),'A'), 
		coalesce(obter_dados_pf(cd_pessoa_fisica,'AL'),0) 
	into STRICT	qt_idade_w, 
		qt_altura_cm_w 
	from	med_cliente 
	where	nr_sequencia = nr_seq_cliente_p;
 
	if (coalesce(qt_idade_w::text, '') = '') then 
		qt_idade_w	:= 0;
	elsif (coalesce(qt_altura_cm_w::text, '') = '') then 
		qt_altura_cm_w	:= 0;
	end if;
	 
	end;	
end if;
 
nr_atendimento_p	:= nr_atendimento_w;
qt_idade_p		:= qt_idade_w;
qt_altura_cm_p		:= qt_altura_cm_w;
 
end;
