SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_evolucao_atribchange ( nr_sequencia_p bigint, dt_evolucao_p timestamp, nr_seq_cliente_p bigint, qt_peso_p INOUT bigint) AS $body$
DECLARE


nr_seq_evolucao_w	bigint	:= 0;
qt_peso_w		real	:= 0;
qt_med_consulta_w		bigint	:=0;


BEGIN

if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	begin

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_evolucao_w
	from	med_evolucao a
	where	a.nr_seq_cliente   = nr_seq_cliente_p
	and	(a.qt_peso IS NOT NULL AND a.qt_peso::text <> '')
	and	a.dt_evolucao = (	SELECT	max(trunc(x.dt_evolucao))
					from	med_evolucao x
					where	x.nr_seq_cliente = a.nr_seq_cliente
					and	x.dt_evolucao <= dt_evolucao_p
					and	(x.qt_peso IS NOT NULL AND x.qt_peso::text <> '')
					and	x.nr_sequencia    <> nr_sequencia_p);

	if (nr_seq_evolucao_w <> 0) then
		select	qt_peso
		into STRICT	qt_peso_w
		from	med_evolucao
		where	nr_sequencia = nr_seq_evolucao_w
		and	(qt_peso IS NOT NULL AND qt_peso::text <> '');
	else
		begin
		select	count(*)
		into STRICT	qt_med_consulta_w
		from	med_consulta
		where	ie_tipo_consulta = 3
		and	nr_seq_cliente = nr_seq_cliente_p;

		if (qt_med_consulta_w <> 0) then
			select	coalesce(qt_peso,0)
			into STRICT	qt_peso_w
			from	med_consulta
			where	ie_tipo_consulta = 3
			and	nr_seq_cliente = nr_seq_cliente_p;
		end if;
		end;
	end if;
	end;
end if;

qt_peso_p	:= qt_peso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE med_evolucao_atribchange ( nr_sequencia_p bigint, dt_evolucao_p timestamp, nr_seq_cliente_p bigint, qt_peso_p INOUT bigint) FROM PUBLIC;

