CREATE OR REPLACE PROCEDURE med_gerar_avaliacao_pac (nr_seq_aval_compl_p bigint, nr_seq_tipo_aval_p bigint, cd_medico_p text, nm_usuario_p text, nr_seq_avaliacao_p INOUT bigint) AS $body$
DECLARE

 
nr_seq_avaliacao_w	bigint;
cd_pessoa_fisica_w	varchar(10);
dt_avaliacao_w		timestamp;


BEGIN 
 
select	coalesce(max(nr_sequencia),0) 
into STRICT	nr_seq_avaliacao_w 
from	med_avaliacao_paciente 
where 	nr_seq_aval_compl	= nr_seq_aval_compl_p 
and	nr_seq_tipo_avaliacao	= nr_seq_tipo_aval_p;
 
 
if (nr_seq_avaliacao_w = 0) then 
	begin 
 
	select	nextval('med_avaliacao_paciente_seq') 
	into STRICT	nr_seq_avaliacao_w 
	;
 
	insert	into med_avaliacao_paciente(nr_sequencia, 
		cd_pessoa_fisica, 
		cd_medico, 
		dt_avaliacao, 
		dt_atualizacao, 
		nm_usuario, 
		ds_observacao, 
		nr_seq_tipo_avaliacao, 
		ie_avaliacao_parcial,	 
		nr_atendimento, 
		nr_prescricao, 
		nr_seq_aval_compl) 
	(SELECT	nr_seq_avaliacao_w, 
		b.cd_pessoa_fisica, 
		cd_medico_p, 
		a.dt_avaliacao, 
		clock_timestamp(), 
		nm_usuario_p, 
		null, 
		nr_seq_tipo_aval_p, 'N', 
		null, null, a.nr_sequencia 
	from	med_cliente b, 
		med_aval_completa_pac a 
	where	a.nr_seq_cliente	= b.nr_sequencia 
	and	a.nr_sequencia		= nr_seq_aval_compl_p);
 
	end;
end if;
 
commit;
 
nr_seq_avaliacao_p	:= nr_seq_avaliacao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
