SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_gerar_encaixe_okclick ( nr_ficha_p text, cd_medico_p text, ie_classif_agenda_p text, dt_agenda_p timestamp, hr_encaixe_p text, nr_minuto_duracao_p text, cd_agenda_p text, nm_usuario_origem_p text, cd_pessoa_fisica_p text, nm_pessoa_fisica_p text, cd_convenio_p bigint, nr_seq_lista_p bigint, nm_usuario_p text) AS $body$
DECLARE

		 
nr_seq_plano_w	bigint;
qt_existe_w	bigint;
		

BEGIN 
 
if (nr_ficha_p IS NOT NULL AND nr_ficha_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then 
	begin 
	 
	select	count(*) 
	into STRICT	qt_existe_w 
	from	med_cliente 
	where	cd_pessoa_sist_orig	= nr_ficha_p 
	and	cd_medico		= cd_medico_p;
	 
	if (qt_existe_w > 0) then 
		select	nr_seq_plano 
		into STRICT	nr_seq_plano_w 
		from	med_cliente 
		where	cd_pessoa_sist_orig	= nr_ficha_p 
		and	cd_medico		= cd_medico_p;
	end if;
	 
	end;
end if;
	 
CALL med_gerar_encaixe(ie_classif_agenda_p,'1', dt_agenda_p, hr_encaixe_p, nr_minuto_duracao_p, cd_agenda_p, nm_usuario_origem_p, cd_pessoa_fisica_p, nm_pessoa_fisica_p, cd_convenio_p, nr_seq_plano_w, nm_usuario_p);
 
/* Se for encaixe da lista de espera */
 
if (nr_seq_lista_p IS NOT NULL AND nr_seq_lista_p::text <> '') then 
	begin	 
	CALL med_excluir_lista_espera(nr_seq_lista_p);	
	end;
end if;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
