CREATE OR REPLACE PROCEDURE med_gerar_exames_protocolo (nr_seq_pedido_p bigint, nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_dados_clinicos_w		varchar(255);
ds_exame_ant_w		varchar(255);
nr_seq_exame_w		bigint;
nr_seq_res_w			bigint;
nr_seq_apresent_w		bigint;
ds_justificativa_w		varchar(255);


c01 CURSOR FOR
	SELECT	nr_seq_exame, nr_seq_apresent
	from	med_exame_protocolo
	where	nr_seq_protocolo	= nr_seq_protocolo_p
	and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
	order by nr_seq_apresent;


BEGIN

select	ds_dados_clinicos,
	ds_exame_anterior,
	ds_justificativa
into STRICT	ds_dados_clinicos_w,
	ds_exame_ant_w,
	ds_justificativa_w
from	med_protocolo_exame
where	nr_sequencia		= nr_seq_protocolo_p;

update	med_pedido_exame
set	ds_dados_clinicos 	= ds_dados_clinicos_w,
	ds_exame_ant		= ds_exame_ant_w,
	ds_justificativa	= ds_justificativa_w
where	nr_sequencia		= nr_seq_pedido_p;

open	c01;
loop
fetch	c01 into nr_seq_exame_w, nr_seq_apresent_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('med_ped_exame_cod_seq')
	into STRICT	nr_seq_res_w
	;

	insert into Med_Ped_Exame_Cod(nr_sequencia,
		nr_seq_pedido,
		dt_atualizacao,
		nm_usuario,
		nr_seq_exame,
		qt_exame,
		nr_seq_apresent)
	values (nr_seq_res_w,
		nr_seq_pedido_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_exame_w,
		1, nr_seq_apresent_w);

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
