CREATE OR REPLACE PROCEDURE med_gerar_laudo_interesse (nr_seq_laudo_p bigint, nr_seq_classif_p bigint, cd_medico_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_classif_w	bigint;

BEGIN

nr_seq_classif_w := null;

if (nr_seq_classif_p > 0) then
	nr_seq_classif_w := nr_seq_classif_p;
end if;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	med_laudo_cdi
where	cd_medico	= cd_medico_p
and	nr_seq_laudo	= nr_seq_laudo_p;

if (nr_sequencia_w <> 0) then
	begin

	update	med_laudo_cdi
	set	nr_seq_classif	= nr_seq_classif_w
	where	cd_medico	= cd_medico_p
	and	nr_seq_laudo	= nr_seq_laudo_p;

	end;
else
	begin

	select	nextval('med_laudo_cdi_seq')
	into STRICT	nr_sequencia_w
	;

	insert 	into med_laudo_cdi(nr_sequencia,
		nr_seq_laudo,
		cd_medico,
		dt_atualizacao,
		nm_usuario,
		ds_laudo,
		nr_seq_classif)
	values (nr_sequencia_w,
		nr_seq_laudo_p,
		cd_medico_p,
		clock_timestamp(),
		nm_usuario_p,
		' ',
		nr_seq_classif_w);

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
