CREATE OR REPLACE PROCEDURE med_gravar_aviso (nm_usuario_refer_p text, dt_aviso_p timestamp, ds_aviso_p text, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	bigint;


BEGIN

if (nm_usuario_refer_p IS NOT NULL AND nm_usuario_refer_p::text <> '') then
	begin
	if (ds_aviso_p IS NOT NULL AND ds_aviso_p::text <> '') then
		begin
		/* Verifica se já existe um registro para esta data e usuário referente*/

		select	count(*)
		into STRICT	qt_existe_w
		from	med_aviso
		where	nm_usuario_refer	= nm_usuario_refer_p
		and		dt_aviso			= dt_aviso_p;

		if (qt_existe_w > 0) then

			update	med_aviso
			set	ds_aviso		= ds_aviso_p,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nm_usuario_refer	= nm_usuario_refer_p
			and	dt_aviso		= dt_aviso_p;
		elsif (qt_existe_w = 0) then

			insert	into	med_aviso(
								nr_sequencia,
								nm_usuario_refer,
								dt_aviso,
								dt_atualizacao,
								nm_usuario,
								ds_aviso)
							values (
								nextval('med_aviso_seq'),
								nm_usuario_refer_p,
								dt_aviso_p,
								clock_timestamp(),
								nm_usuario_p,
								ds_aviso_p);
		end if;
		end;
	else
		begin
		delete
		from	med_aviso
		where	nm_usuario_refer = nm_usuario_refer_p
		and	dt_aviso = dt_aviso_p;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
