SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_incluir_exame_padrao (cd_medico_p text, ds_exame_p text, nm_usuario_p text, nr_seq_exame_p INOUT bigint) AS $body$
DECLARE


nr_seq_grupo_w		bigint;
nr_seq_exame_w		bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_exame_w
from	med_exame_padrao
where	cd_medico		= cd_medico_p
and	upper(ds_exame)		= ds_exame_p;

if (nr_seq_exame_w 	= 0) then
	begin

	select	nextval('med_exame_padrao_seq')
	into STRICT	nr_seq_exame_w
	;

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_grupo_w
	from	med_grupo_exame
	where	cd_medico		= cd_medico_p
	and	upper(ds_grupo_exame) 	= 'IMPORTADO';

	if (nr_seq_grupo_w	= 0) then
		begin

		select	nextval('med_grupo_exame_seq')
		into STRICT	nr_seq_grupo_w
		;

		insert	into med_grupo_exame(nr_sequencia,
			ds_grupo_exame,
			cd_medico,
			dt_atualizacao,
			nm_usuario)
		values (nr_seq_grupo_w,
			'IMPORTADO',
			cd_medico_p,
			clock_timestamp(),
			nm_usuario_p);

		end;
	end if;

	insert	into med_exame_padrao(nr_sequencia,
		ie_pedido,
		ie_resultado,
		dt_atualizacao,
		nm_usuario,
		ds_exame,
		cd_medico,
		ie_formato_resultado,
		qt_decimais,
		nr_seq_grupo)
	values (nr_seq_exame_w,
		'S', 'S', clock_timestamp(), nm_usuario_p,
		ds_exame_p, cd_medico_p,
		'V', 2, nr_seq_grupo_w);

	end;
end if;

commit;

nr_seq_exame_p	:= nr_seq_exame_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
