CREATE OR REPLACE PROCEDURE med_inserir_agenda_pessoal (cd_medico_p text, dt_agenda_p timestamp, nr_minuto_duracao_p bigint, ds_agenda_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_agenda_w		bigint;
nm_usuario_ref_w	varchar(15);


BEGIN

select	coalesce(max(nm_usuario),'0')
into STRICT	nm_usuario_ref_w
from	usuario
where	cd_pessoa_fisica	= cd_medico_p;

if (nm_usuario_ref_w <> '0') then
	begin

	select	nextval('agenda_tasy_seq')
	into STRICT	nr_seq_agenda_w
	;


	insert	into agenda_tasy(nr_sequencia,
		ds_agenda,
		dt_agenda,
		nr_seq_tipo,
		nr_minuto_duracao,
		nm_usuario_agenda,
		dt_atualizacao,
		nm_usuario,
		ds_observacao,
		ds_local,
		ie_status)
	values (nr_seq_agenda_w,
		substr(ds_agenda_p,1,100),
		dt_agenda_p, 2,
		nr_minuto_duracao_p,
		nm_usuario_ref_w,
		clock_timestamp(), nm_usuario_p,
		substr(ds_agenda_p,1,255),
		null, 'M');
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
