CREATE OR REPLACE PROCEDURE med_inserir_agenda (cd_medico_p text, dt_agenda_p timestamp, ds_tipo_agenda_p text, nr_ficha_p bigint, nr_telefone_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

cd_agenda_w		bigint;
nr_minuto_interv_w	bigint;
cd_pessoa_fisica_w	varchar(10);
nr_sequencia_w		bigint;
ie_classif_agenda_w	varchar(05);
nr_seq_classif_med_w	bigint;
qt_agenda_w		bigint;
ie_status_agenda_w	varchar(01);
qt_agenda_pac_w	bigint;

BEGIN
 
if (ds_tipo_agenda_p	= '') then 
	begin 
	ie_classif_agenda_w	:= 'N';
	nr_seq_classif_med_w	:= null;
	end;
else 
	begin 
	if (ds_tipo_agenda_p	= 'CONS') or (ds_tipo_agenda_p	= 'CONS.QT') then 
		begin 
		ie_classif_agenda_w	:= 'C';
		if (ds_tipo_agenda_p	= 'CONS') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'CONSULTA';
		end if;
		end;
	elsif (ds_tipo_agenda_p	= 'RET') or (ds_tipo_agenda_p	= 'RET PRO') or (ds_tipo_agenda_p	= 'RET. DE QT') or (ds_tipo_agenda_p	= 'RET CIRUR') or (ds_tipo_agenda_p	= 'RET COLO') or (ds_tipo_agenda_p	= 'RET EDA') or (ds_tipo_agenda_p	= 'RET PONTOS') or (ds_tipo_agenda_p	= 'RECONSULTA') or (ds_tipo_agenda_p	= 'RET T.E') or (ds_tipo_agenda_p	= 'RECON') then 
		begin 
		ie_classif_agenda_w	:= 'R';
		if (ds_tipo_agenda_p	= 'RET') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'RETORNO';
		elsif (ds_tipo_agenda_p	= 'RET PRO') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'RETORNO DA PRO VIDA';
		elsif (ds_tipo_agenda_p	= 'RET. DE QT') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'RET. QT';
		elsif (ds_tipo_agenda_p	= 'RET CIRUR') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'RETORNO DE CIRURGIA';
		elsif (ds_tipo_agenda_p	= 'RET COLO') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'RETOSRNO DE COLONOSCOPIA';
		elsif (ds_tipo_agenda_p	= 'RET EDA') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'RETORNO DE ENDOSCOPIA';
		elsif (ds_tipo_agenda_p	= 'RET PONTOS') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'RETIRADA DE PONTOS';
		elsif (ds_tipo_agenda_p	= 'RECONSULTA') OR (ds_tipo_agenda_p	= 'RECON') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'RECONSULTA';
		elsif (ds_tipo_agenda_p	= 'RET T.E') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_classif_med_w 
			from	med_classif_agenda 
			where	cd_medico			= cd_medico_p 
			and	upper(ds_classif_agenda)	= 'RETORNO DE TESTE ERGOMETRICO';
		end if;
		end;
	else 
		ie_classif_agenda_w	:= 'X';
	end if;
	end;
end if;
select	coalesce(max(cd_agenda),0), 
	coalesce(max(nr_minuto_intervalo),0) 
into STRICT	cd_agenda_w, 
	nr_minuto_interv_w 
from	agenda 
where	cd_pessoa_fisica	= cd_medico_p 
and	cd_estabelecimento	= 1;
if (cd_agenda_w <> 0) then 
	begin 
	 
	delete from agenda_consulta 
	where	cd_agenda		= cd_agenda_w 
	and	ie_status_agenda	= 'L' 
	and	dt_agenda		= dt_agenda_p;
 
 
	select	coalesce(max(cd_pessoa_fisica),'0') 
	into STRICT	cd_pessoa_fisica_w 
	from	med_cliente 
	where	cd_pessoa_sist_orig	= nr_ficha_p 
	and	cd_medico		= cd_medico_p;		
	if (cd_pessoa_fisica_w <> '0') then 
		begin 
 
		select	count(*) 
		into STRICT	qt_agenda_pac_w 
		from	agenda_consulta 
		where	cd_agenda		= cd_agenda_w 
		and	dt_agenda		= dt_agenda_p 
		and	cd_pessoa_fisica	= cd_pessoa_fisica_w;
 
		if (qt_agenda_pac_w = 0) then 
			begin 
			select	nextval('agenda_consulta_seq') 
			into STRICT	nr_sequencia_w 
			;
			select	count(*) 
			into STRICT	qt_agenda_w 
			from	agenda_consulta 
			where	cd_agenda	= cd_agenda_w 
			and 	dt_agenda 	= dt_agenda_p;
			if (qt_agenda_w = 0)then 
				ie_status_agenda_w	:= 'N';
			elsif (qt_agenda_w = 1)then 
				ie_status_agenda_w	:= 'E';
			elsif (qt_agenda_w = 2)then 
				ie_status_agenda_w	:= 'C';
			elsif (qt_agenda_w = 3)then 
				ie_status_agenda_w	:= 'M';
			elsif (qt_agenda_w = 3)then 
				ie_status_agenda_w	:= 'O';
			end if;
			insert	into agenda_consulta(nr_sequencia, 
				cd_agenda, 
				dt_agenda, 
				ie_status_agenda, 
				ie_classif_agenda, 
				nm_usuario, 
				dt_atualizacao, 
				nr_minuto_duracao, 
				cd_pessoa_fisica, 
				nr_telefone, 
				ds_observacao, 
				nr_seq_classif_med) 
			values (nr_sequencia_w, 
				cd_agenda_W, 
				dt_agenda_p, 
				ie_status_agenda_w,		 
				ie_classif_agenda_w, 
				nm_usuario_p, 
				clock_timestamp(), 
				nr_minuto_interv_w, 
				cd_pessoa_fisica_w, 
				nr_telefone_p, 
				ds_observacao_p, 
				nr_seq_classif_med_w);
 
			end;
		end if;
 
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
