SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_inserir_alerta (cd_medico_p text, nr_ficha_p bigint, ds_alerta_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cliente_w	bigint;
nr_seq_alerta_w		bigint;



BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_cliente_w
from	med_cliente
where	cd_medico		= cd_medico_p
and	cd_pessoa_sist_orig	= nr_ficha_p;

if (nr_seq_cliente_w <> 0) then
	begin

	select	nextval('med_alerta_seq')
	into STRICT	nr_seq_alerta_w
	;


	insert	into med_alerta(nr_sequencia,
		nr_seq_cliente,
		dt_alerta,
		ie_tipo_alerta,
		dt_atualizacao,
		nm_usuario,
		ds_alerta)
	values (nr_seq_alerta_w,
		nr_seq_cliente_w,
		clock_timestamp(),'M',
		clock_timestamp(),
		nm_usuario_p,
		substr(ds_alerta_p,1,255));
	end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
