CREATE OR REPLACE PROCEDURE med_inserir_cid (cd_medico_p text, nr_ficha_p bigint, cd_cid_p text, dt_cid_p timestamp, ds_cid_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cliente_w	bigint;
nr_seq_CID_w		bigint;
cd_cid_w		varchar(10);


BEGIN

cd_cid_w		:= replace(replace(replace(cd_cid_p, '.', ''), '/', ''), ' ', '');


select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_cliente_w
from	med_cliente
where	cd_medico		= cd_medico_p
and	cd_pessoa_sist_orig	= nr_ficha_p;

select	coalesce(max(cd_doenca_cid),'0')
into STRICT	cd_cid_w
from	cid_doenca
where	upper(cd_doenca_cid) = upper(cd_cid_w);


if (nr_seq_cliente_w > 0) and (cd_cid_w <> '0') then
	begin

	select	nextval('med_pac_cid_seq')
	into STRICT	nr_seq_CID_w
	;


	insert	into med_pac_cid(nr_sequencia,
		nr_seq_cliente,
		cd_doenca_cid,
		dt_atualizacao,
		nm_usuario,
		ds_observacao,
		dt_diagnostico,
		ie_classificacao,
		ie_status)
	values (nr_seq_CID_w,
		nr_seq_cliente_w,
		upper(cd_cid_w),
		clock_timestamp(), nm_usuario_p,
		ds_cid_p,
		coalesce(dt_cid_p, trunc(clock_timestamp())),
		'S',
		'A');


	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
