CREATE OR REPLACE PROCEDURE med_inserir_contatos (cd_medico_p text, nm_contato_p text, nr_telefone_p text, ds_profissao_p text, ds_observacao_p text, ds_endereco_p text, cd_cep_p text, ds_bairro_p text, ds_municipio_p text, sg_estado_p text, ds_complemento_p text, ie_publico_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_contato_w	bigint;
nr_seq_end_w		bigint;
nm_usuario_ref_w	varchar(15);


BEGIN

	select	coalesce(max(nm_usuario),'0')
	into STRICT	nm_usuario_ref_w
	from	usuario
	where	cd_pessoa_fisica	= cd_medico_p;


	if (nm_usuario_ref_w <> '0') then
		begin

		select	nextval('usuario_contato_seq')
		into STRICT	nr_seq_contato_w
		;

		insert	into usuario_contato(nr_sequencia,
			nm_usuario_ref,
			nm_contato,
			dt_atualizacao,
			nm_usuario,
			dt_nascimento,
			nr_telefone_celular,
			nm_conjuge,
			nm_filho,
			ds_email,
			ds_observacao,
			ds_profissao,
			ds_complemento,
			ie_publico)
		values (nr_seq_contato_w,
			nm_usuario_ref_w,
			substr(nm_contato_p,1,80),
			clock_timestamp(),
			nm_usuario_p,
			null,
			substr(nr_telefone_p,1,80),
			null,
			null,
			null,
			substr(ds_observacao_p,1,2000),
			substr(ds_profissao_p,1,40),
			substr(ds_complemento_p,1,40),
			ie_publico_p);

		select	nextval('usuario_contato_end_seq')
		into STRICT	nr_seq_end_w
		;

		insert	into usuario_contato_end(nr_sequencia,
			nr_seq_contato,
			dt_atualizacao,
			nm_usuario,
			ie_tipo_complemento,
			ds_endereco,
			cd_cep,
			ds_bairro,
			ds_municipio,
			sg_estado)
		values (nr_seq_end_w,
			nr_seq_contato_w,
			clock_timestamp(),
			nm_usuario_p, 1,
			substr(ds_endereco_p,1,40),
			substr(cd_cep_p,1,15),
			substr(ds_bairro_p,1,40),
			substr(ds_municipio_p,1,40),
			substr(sg_estado_p,1,15));

		end;
	end if;

commit;

end;
$body$
