SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_inserir_data_consulta (nr_seq_cliente_p bigint, ie_opcao_p text) AS $body$
DECLARE


/* ie_opcao_p

PU - Atualiza a primeira e a ultima data de consulta
U - Atualiza somente a primeira data de consulta
*/
BEGIN

if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	begin

	if (ie_opcao_p = 'PU') then
		update	med_cliente
		set	dt_primeira_consulta	= trunc(clock_timestamp()),
			dt_ultima_consulta	= trunc(clock_timestamp())
		where	nr_sequencia		= nr_seq_cliente_p;
	elsif (ie_opcao_p = 'U') then
		update	med_cliente
		set	dt_ultima_consulta	= trunc(clock_timestamp())
		where	nr_sequencia		= nr_seq_cliente_p;
	end if;

	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
