SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_inserir_diagnostico (cd_medico_p text, nr_ficha_p bigint, ds_diagnostico_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cliente_w	bigint;
ds_diagnostico_w	varchar(4000);


BEGIN


select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_cliente_w
from	med_cliente
where	cd_medico		= cd_medico_p
and	cd_pessoa_sist_orig	= nr_ficha_p;

if (nr_seq_cliente_w > 0) then
	begin

	select	coalesce(max(ds_diagnostico), '0')
	into STRICT	ds_diagnostico_w
	from	med_pac_diagnostico
	where	nr_seq_cliente	= nr_seq_cliente_w;

	if (ds_diagnostico_w = '0') then


		insert	into med_pac_diagnostico(nr_seq_cliente,
			ds_diagnostico,
			dt_atualizacao,
			nm_usuario,
			dt_diagnostico)
		values (nr_seq_cliente_w,
			ds_diagnostico_p,
			clock_timestamp(), nm_usuario_p,clock_timestamp());
	else

		ds_diagnostico_w 	:= ds_diagnostico_w || ds_diagnostico_p;

		update	med_pac_diagnostico
		set	ds_diagnostico	= substr(ds_diagnostico_w,1,2000)
		where	nr_seq_cliente	= nr_seq_cliente_w;

	end if;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
