CREATE OR REPLACE PROCEDURE med_inserir_exames_padroes ( nr_seq_pedido_p bigint, ds_resultado_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_apresent_w	bigint;
ds_lista_w		varchar(2000);
ds_resultado_w		varchar(2000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;


BEGIN
if (nr_seq_pedido_p IS NOT NULL AND nr_seq_pedido_p::text <> '') and (ds_resultado_p IS NOT NULL AND ds_resultado_p::text <> '') then
	begin
	ds_lista_w	:= ds_resultado_p;
	while	(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		begin
		tam_lista_w		:= length(ds_lista_w);
		ie_pos_virgula_w		:= position(',' in ds_lista_w);


		if (ie_pos_virgula_w <> 0) then

			ds_resultado_w	:= substr(ds_lista_w,1,(ie_pos_virgula_w - 1));
			ds_lista_w	:= substr(ds_lista_w,(ie_pos_virgula_w + 1), tam_lista_w);
		else
			ds_lista_w	:= null;
		end if;

		if ((trim(both ds_resultado_w) IS NOT NULL AND (trim(both ds_resultado_w))::text <> '')) and (trim(both ds_resultado_w) <> ',') then
			begin
			select	coalesce(d.nr_seq_apresent, 0)
			into STRICT	nr_seq_apresent_w
			from	med_grupo_exame   d,
				med_exame_padrao  c
			where	c.nr_seq_grupo = d.nr_sequencia
			and	c.nr_sequencia = ds_resultado_w;

			insert into Med_Ped_Exame_Cod(
				nr_sequencia,
				nr_seq_pedido,
				dt_atualizacao,
				nm_usuario,
				nr_seq_exame,
				qt_exame,
				nr_seq_apresent)
			values (nextval('med_ped_exame_cod_seq'),
				nr_seq_pedido_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_resultado_w,
				1,
				nr_seq_apresent_w);
			end;
		end if;
		end;
	end loop;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
