SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_inserir_itens_result (nr_seq_tipo_p bigint, ds_tipo_p text, nr_seq_item_res_p bigint, ds_item_res_p text, ds_resultado_p text, qt_resultado_p bigint, nr_seq_item_ref_res_p bigint, ds_item_ref_res_p text, ds_result_ref_res_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_tipo_w		bigint;
nr_seq_item_w		bigint;
nr_seq_res_w		integer;
nr_seq_item_ref_w	bigint	:= null;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_tipo_w
from	med_tipo_avaliacao
where	ds_tipo		= 'COPIA ' || ds_tipo_p
and	nm_usuario_resp	= nm_usuario_p;

if (nr_seq_tipo_w > 0) then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_item_w
	from	med_item_avaliar
	where	ds_item		= ds_item_res_p
	and	nr_seq_tipo	= nr_seq_tipo_w;


	if (nr_seq_item_w > 0) then
		begin

		select	coalesce(max(nr_seq_res),0) + 1
		into STRICT	nr_seq_res_w
		from	med_item_avaliar_res
		where	nr_seq_item	= nr_seq_item_w;


		if (ds_item_ref_res_p IS NOT NULL AND ds_item_ref_res_p::text <> '') then
			begin

			select	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_item_ref_w
			from	med_item_avaliar
			where	ds_item		= ds_item_ref_res_p
			and	nr_seq_tipo	= nr_seq_tipo_w;

			if (nr_seq_item_ref_w = 0) then
				nr_seq_item_ref_w	:= null;
			end if;

			end;
		end if;


		insert	into med_item_avaliar_res(nr_seq_item,
			nr_seq_res,
			dt_atualizacao,
			nm_usuario,
			ds_resultado,
			qt_resultado,
			nr_seq_item_ref,
			ds_result_ref)
		values (nr_seq_item_w,
			nr_seq_res_w,
			clock_timestamp(),
			nm_usuario_p,
			ds_resultado_p,
			qt_resultado_p,
			nr_seq_item_ref_w,
			ds_result_ref_res_p);

		end;
	end if;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
