CREATE OR REPLACE PROCEDURE med_inserir_pac_espera (nr_seq_agenda_p bigint, nr_seq_lista_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);
cd_convenio_w		bigint;
nm_contato_w		varchar(30);
nm_pessoa_fisica_w	varchar(80);
nr_telefone_w		varchar(15);
nr_seq_plano_w	bigint;


BEGIN 
 
select	cd_pessoa_fisica, 
	obter_nome_pf(cd_pessoa_fisica), 
	cd_convenio, 
	nm_contato, 
	nr_telefone, 
	nr_seq_plano 
into STRICT	cd_pessoa_fisica_w, 
	nm_pessoa_fisica_w, 
	cd_convenio_w, 
	nm_contato_w, 
	nr_telefone_w, 
	nr_seq_plano_w 
from	med_lista_espera 
where	nr_sequencia	= nr_seq_lista_p;
 
update	agenda_consulta 
set	ie_status_agenda	= 'N', 
	cd_pessoa_fisica	= cd_pessoa_fisica_w, 
	cd_convenio		= cd_convenio_w, 
	nm_pessoa_contato	= nm_contato_w, 
	nr_telefone		= nr_telefone_w, 
	ie_status_paciente	= '1', 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp(), 
	nm_paciente		= nm_pessoa_fisica_w, 
	nr_seq_plano		= nr_seq_plano_w 
where	nr_sequencia		= nr_seq_agenda_p;
 
delete	from med_lista_espera 
where	nr_sequencia		= nr_seq_lista_p;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
