CREATE OR REPLACE PROCEDURE med_inserir_receita (cd_medico_p text, nr_ficha_p bigint, dt_receita_p timestamp, ds_receita_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_receita_w	bigint;
nr_seq_cliente_w	bigint;
cd_pessoa_fisica_w	varchar(10);


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_cliente_w
from	med_cliente
where	cd_medico		= cd_medico_p
and	cd_pessoa_sist_orig	= nr_ficha_p;


if (nr_seq_cliente_w > 0) then
	begin

	select	nextval('med_receita_seq')
	into STRICT	nr_seq_receita_w
	;

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	med_cliente
	where	nr_sequencia	= nr_seq_cliente_w;

	insert	into med_receita(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_receita,
		ds_receita,
		nr_seq_cliente,
		cd_pessoa_fisica)
	values (nr_seq_receita_w,
		clock_timestamp(),
		nm_usuario_p,
		dt_receita_p,
		coalesce(ds_receita_p, ' '),
		nr_seq_cliente_w,
		cd_pessoa_fisica_w);

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
