CREATE OR REPLACE PROCEDURE med_inserir_result_exames2 (cd_medico_p text, nr_ficha_p text, dt_resultado_p timestamp, ds_exame_p text, vl_result_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_exame_w		bigint;
nr_seq_resultado_w	bigint;
nr_seq_cliente_w	bigint;


BEGIN


select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_cliente_w
from	med_cliente
where	cd_medico		= cd_medico_p
and	cd_pessoa_sist_orig	= nr_ficha_p;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_exame_w
from	med_exame_padrao
where	cd_medico	= cd_medico_p
and	ds_exame	= ds_exame_p;


if (nr_seq_exame_w = 0) then
	nr_seq_exame_w := Med_Incluir_Exame_Padrao(cd_medico_p, ds_exame_p, nm_usuario_p, nr_seq_exame_w);
end if;

if (nr_seq_exame_w > 0) then
	begin

	delete	from med_result_exame
	where	nr_seq_exame	= nr_seq_exame_w
	and	nr_seq_cliente	= nr_seq_cliente_w
	and	dt_exame	= dt_resultado_p;


	select	nextval('med_result_exame_seq')
	into STRICT	nr_seq_resultado_w
	;

	insert	into med_result_exame(nr_sequencia,
		nr_seq_exame,
		dt_exame,
		dt_atualizacao,
		nm_usuario,
		vl_exame,
		nr_seq_cliente)
	values (nr_seq_resultado_w,
		nr_seq_exame_w,
		dt_resultado_p,
		clock_timestamp(),
		nm_usuario_p,
		vl_result_p,
		nr_seq_cliente_w);

	end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
