CREATE OR REPLACE PROCEDURE med_inserir_textos_padroes (cd_estabelecimento_p bigint, cd_medico_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_texto_w		bigint;
ds_texto_w		varchar(4000);
ds_titulo_w		varchar(255);
ie_tipo_texto_w		varchar(02);
ds_dados_clinicos_w	varchar(255);

c01 CURSOR FOR
	SELECT	a.ds_titulo,
		a.ds_texto,
		a.ie_tipo_texto,
		a.ds_dados_clinicos
	from	med_texto_padrao_hosp a
	where	a.cd_estabelecimento	= cd_estabelecimento_p
	and	not exists (SELECT	1
		from	med_texto_padrao x
		where	x.cd_medico	= cd_medico_p
		and	x.ds_titulo	= a.ds_titulo);


BEGIN

open	c01;
loop
fetch	c01 into
	ds_titulo_w,
	ds_texto_w,
	ie_tipo_texto_w,
	ds_dados_clinicos_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('med_texto_padrao_seq')
	into STRICT	nr_seq_texto_w
	;

	insert	into med_texto_padrao(nr_sequencia,
		cd_medico,
		ds_texto,
		ds_titulo,
		dt_atualizacao,
		nm_usuario,
		ie_tipo_texto,
		ds_dados_clinicos)
	values (nr_seq_texto_w,
		cd_medico_p,
		ds_texto_w,
		ds_titulo_w,
		clock_timestamp(),
		nm_usuario_p,
		ie_tipo_texto_w,
		ds_dados_clinicos_w);

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
