CREATE OR REPLACE PROCEDURE med_localizar_texto_evolucao ( nr_seq_cliente_p bigint, texto_p text, sequencias_p INOUT text) AS $body$
DECLARE


nr_sequencia_w 	bigint;
ds_evolucao_w 	text;
sequencias_w 	varchar(200)	:= '';
ie_achou_w	varchar(1);


C01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_evolucao
	from	med_evolucao
	where	nr_seq_cliente = nr_seq_cliente_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	ds_evolucao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	--Verificar se possui o texto
	ie_achou_w := obter_se_long_contem('med_evolucao', 'ds_evolucao', 'where nr_sequencia = :nr_sequencia', 'nr_sequencia='||nr_sequencia_w, texto_p, ie_achou_w);
	if ( ie_achou_w	= 'S') then
		sequencias_w :=  nr_sequencia_w||','|| sequencias_w;
	end if;
	end;
end loop;
close C01;

sequencias_p := sequencias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
