CREATE OR REPLACE PROCEDURE med_obter_cliente_prim_cons ( cd_pessoa_fisica_p text, cd_medico_p text, nr_seq_cliente_p INOUT bigint, qt_primeira_consulta_p INOUT bigint) AS $body$
DECLARE


nr_seq_cliente_w	bigint := 0;
qt_primeira_consulta_w	bigint := 0;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	begin

	select	max(coalesce(nr_sequencia,0))
	into STRICT	nr_seq_cliente_w
	from	med_cliente
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	cd_medico		= cd_medico_p;

	if (nr_seq_cliente_w <> 0) then
		begin

		select	count(*)
		into STRICT	qt_primeira_consulta_w
		from	med_cliente
		where	(dt_primeira_consulta IS NOT NULL AND dt_primeira_consulta::text <> '')
		and	nr_sequencia = nr_seq_cliente_w;

		end;
	end if;

	end;
end if;

nr_seq_cliente_p	:= nr_seq_cliente_w;
qt_primeira_consulta_p	:= qt_primeira_consulta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
