SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE med_pessoa_fisica_afterpost ( cd_pessoa_fisica_p text, nm_pessoa_fisica_p text, cd_medico_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w		bigint;
nr_seq_cliente_w	bigint;
vl_parametro_w		varchar(255);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	med_cliente
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	cd_medico		= cd_medico_p;

	if (qt_registro_w = 0) then
		begin
		CALL med_incluir_paciente_cliente(cd_pessoa_fisica_p, cd_medico_p, nm_usuario_p);

		select	coalesce(nr_sequencia,0)
		into STRICT	nr_seq_cliente_w
		from	med_cliente
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	cd_medico		= cd_medico_p;

		if (nr_seq_cliente_w > 0) then
			CALL med_ultima_atualizacao_cliente(nr_seq_cliente_w, cd_pessoa_fisica_p, cd_medico_p, 'C');
		end if;
		end;
	end if;
	vl_parametro_w := Obter_Param_Usuario(9007, 59, wheb_usuario_pck.get_cd_perfil, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, vl_parametro_w);

	if (vl_parametro_w = 'S') then
		CALL med_altera_inicial_nome_pac(cd_pessoa_fisica_p, obter_initcap(nm_pessoa_fisica_p));
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
