CREATE OR REPLACE PROCEDURE med_salvar_agenda_consulta ( nr_seq_plano_p bigint, cd_medico_p text, cd_convenio_p bigint, nr_seq_agenda_p bigint, cd_agenda_p bigint, cd_pessoa_fisica_p text, dt_agenda_p timestamp, nr_seq_plano_conv_p INOUT text, ds_erro_p INOUT text, nm_usuario_p text) AS $body$
DECLARE

 
qt_registro_w	bigint;				
ds_erro_w	varchar(255);			
 

BEGIN 
 
ds_erro_w := med_consistir_agenda_consulta(nr_seq_agenda_p, cd_agenda_p, cd_pessoa_fisica_p, dt_agenda_p, nm_usuario_p, ds_erro_w);
 
if (coalesce(ds_erro_w::text, '') = '') then 
	if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') and (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then 
		begin 
		select	count(*) 
		into STRICT	qt_registro_w 
		from	med_plano 
		where	cd_medico = cd_medico_p 
		and	cd_convenio = cd_convenio_p 
		and	nr_sequencia = nr_seq_plano_p 
		and	ie_situacao = 'A';
 
		if (qt_registro_w = 0) then 
			nr_seq_plano_conv_p := '';
		end if;
		end;	
	end if;
elsif (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then 
	ds_erro_p	:= substr(ds_erro_w,1,255);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
