CREATE OR REPLACE PROCEDURE med_transferir_paciente ( cd_medico_origem_p text, cd_medico_destino_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p > 0) and (cd_medico_destino_p > 0) then
	update	med_cliente
	set	cd_medico		=	cd_medico_destino_p,
		cd_pessoa_sist_orig	=	(SELECT coalesce(max(coalesce(campo_numerico(cd_pessoa_sist_orig),0)),0) + 1 from med_cliente where cd_medico = cd_medico_destino_p)
	where	nr_sequencia		=	nr_sequencia_p;
elsif (cd_medico_origem_p > 0) and (cd_medico_destino_p > 0) then
	update	med_cliente
	set	cd_medico		=	cd_medico_destino_p,
		cd_pessoa_sist_orig	=	(SELECT coalesce(max(coalesce(campo_numerico(cd_pessoa_sist_orig),0)),0) + 1 from med_cliente where cd_medico = cd_medico_destino_p)
	where	cd_medico		=	cd_medico_origem_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
