CREATE OR REPLACE PROCEDURE med_treatment_change_status (nr_sequencia_p bigint, status_p text) AS $body$
BEGIN

		update med_treatment_plan
		set ie_patient_status = status_p,
		ie_situacao = CASE WHEN status_p='C' THEN  'I'  ELSE ie_situacao END
		where nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
