CREATE OR REPLACE PROCEDURE med_ultima_atualizacao_cliente ( nr_seq_cliente_p bigint, cd_pessoa_fisica_p text, cd_medico_p text, ie_opcao_p text) AS $body$
DECLARE


/* ie_opcao_p
C - cliente
P - Pessoa f√≠sica
*/
BEGIN

if	((ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and
	 ((nr_seq_cliente_p <> 0) or
	 (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND cd_medico_p IS NOT NULL AND cd_medico_p::text <> ''))) then
	begin

	if (ie_opcao_p = 'C') then
		update	med_cliente
		set	dt_ultima_atualiz	= trunc(clock_timestamp())
		where	nr_sequencia	= nr_seq_cliente_p;
	elsif (ie_opcao_p = 'P') then
		update	med_cliente
                set	dt_ultima_visualiz		= trunc(clock_timestamp())
                where	cd_pessoa_fisica		= cd_pessoa_fisica_p
                and	cd_medico		= cd_medico_p;
	end if;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
