SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE merge_plan_medic (nr_seq_versao_p bigint, nr_seq_medic_p bigint, nm_usuario_p text) AS $body$
DECLARE

											

cd_material_w				bigint;
ie_existe_medic_w			varchar(1);


BEGIN

select 	max(cd_material)
into STRICT	cd_material_w
from  	plano_versao_medic
where 	nr_sequencia = nr_seq_medic_p;

select 	coalesce(max('S'),'N')
into STRICT	ie_existe_medic_w
from	plano_versao_medic
where	nr_seq_versao = nr_seq_versao_p
and		cd_material = cd_material_w;


if (ie_existe_medic_w = 'N')then
	insert into plano_versao_medic(
				nr_sequencia,
				cd_material,
				ds_medicamento,
				cd_unidade_medida,
				ds_dose_diferenciada,
				qt_dose_manha,
				qt_dose_almoco,
				qt_dose_tarde,
				qt_dose_noite,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_versao,
				cd_pessoa_fisica,
				dt_registro,
				ds_potencia,
				ds_forma_medicamento,
				ie_classificacao,
				ds_descricao_complementar,
				ds_observacao,
				ds_motivo,
				ds_orientacao,
				cd_profissional,
				ie_tipo_medicamento,
				ds_princ_ativo,
				ds_horario_especial,
				ds_forma_desc,
				ds_unid_med_desc,
				ie_exibir_plano_medic)				
	SELECT 		nextval('plano_versao_medic_seq'),
				cd_material,
				ds_medicamento,
				cd_unidade_medida,
				ds_dose_diferenciada,
				qt_dose_manha,
				qt_dose_almoco,
				qt_dose_tarde,
				qt_dose_noite,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_p,
				nr_seq_versao_p,
				cd_pessoa_fisica,
				clock_timestamp(),
				ds_potencia,
				ds_forma_medicamento,
				ie_classificacao,
				ds_descricao_complementar,
				ds_observacao,
				ds_motivo,
				ds_orientacao,
				obter_pf_usuario_ativo,
				ie_tipo_medicamento,
				ds_princ_ativo,
				ds_horario_especial,
				ds_forma_desc,
				ds_unid_med_desc,
				'N'
				from  plano_versao_medic
				where nr_sequencia = nr_seq_medic_p;
	commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
