CREATE OR REPLACE PROCEDURE microfilmar_caixa_prontuario ( nr_seq_caixa_p bigint, nm_usuario_p text , cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_parametro_w	varchar(10) := obter_valor_param_usuario(941,103,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p);

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	same_prontuario
	where	nr_seq_caixa	=	nr_seq_caixa_p
	and	coalesce(nr_seq_agrup::text, '') = ''
	and	((ds_parametro_w = 'N') or (cd_estabelecimento = cd_estabelecimento_p));


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	same_prontuario
	set	IE_MICROFILMADO    = 'S'
	where	nr_sequencia = nr_sequencia_w;
	commit;

	end;
END LOOP;
CLOSE C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
