CREATE OR REPLACE PROCEDURE microfilmar_pront_js ( nm_filme_p text, nm_fotograma_p text, nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = '1') then

	update 	same_prontuario
	set    	cd_filme     =  nm_filme_p,
		cd_fotograma =  nm_fotograma_p,
		ie_microfilmado =  'S'
	where  	nr_sequencia =  nr_sequencia_p;

else
	update 	same_prontuario
	set 	ie_microfilmado = 'S'
	where 	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
