DECLARE




				

medic_controlado_w	imp_medic_controlado%rowtype;				

qt_reg_w		bigint;				


BEGIN





select	count(1)

into STRICT	qt_reg_w

from	imp_medic_controlado

where	cd_material	= cd_material_p
and nr_seq_mims_version = nr_mims_ver_p;



if (qt_reg_w	= 0) then

	

	select	nextval('medic_controlado_seq')

	into STRICT	medic_controlado_w.nr_sequencia

	;

	

