	medic_controlado_w.nm_usuario		:= nm_usuario_p;

	medic_controlado_w.cd_classificacao	:= cd_classificacao_p;

	medic_controlado_w.IE_SITUACAO		:= 'A';

	medic_controlado_w.NR_SEQ_MIMS_VERSION := nr_mims_ver_p;

	insert into imp_medic_controlado values  (medic_controlado_w.*);

end if;



end;
