CREATE OR REPLACE PROCEDURE mkt_define_vigenc_prod (CD_PROD_P bigint, TIPO_VIGENC_P text, NM_USUARIO_P text) AS $body$
BEGIN

  BEGIN

    IF TIPO_VIGENC_P = 'I' THEN
      UPDATE MKT_PRODUTO
	    SET DT_INICIO_VIGENCIA = clock_timestamp(), DT_ATUALIZACAO = clock_timestamp(), NM_USUARIO = NM_USUARIO_P
	    WHERE NR_SEQUENCIA = CD_PROD_P;
    END IF;

    IF TIPO_VIGENC_P = 'F' THEN
      UPDATE MKT_PRODUTO
	    SET DT_FIM_VIGENCIA = clock_timestamp(), DT_ATUALIZACAO = clock_timestamp(), NM_USUARIO = NM_USUARIO_P
	    WHERE NR_SEQUENCIA = CD_PROD_P;
    END IF;

  END;

END;
