CREATE OR REPLACE PROCEDURE mob_notif_update_visualizado ( ie_tipo_p text, cd_medico_p text, dt_visualizacao_p INOUT timestamp) AS $body$
BEGIN
dt_visualizacao_p := clock_timestamp();

if (ie_tipo_p IS NOT NULL AND ie_tipo_p::text <> '' AND cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	update	mob_notificacao_pend
	set		dt_visualizacao = dt_visualizacao_p
	where	ie_tipo = ie_tipo_p
	and		cd_medico_resp = cd_medico_p
	and		coalesce(dt_visualizacao::text, '') = '';

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
