CREATE OR REPLACE PROCEDURE movto_banco_pend_baixa_b_post ( nr_seq_cheque_p bigint, nr_adiantamento_p bigint, vl_baixa_p bigint, nr_seq_conv_receb_p bigint, dt_baixa_p timestamp, ie_considerar_p text, nm_usuario_p text) AS $body$
DECLARE


vl_adiantamento_w	double precision;
vl_baixa_w		double precision;
vl_cheque_w		double precision;
vl_recebimento_w	double precision;
vl_multa_w		double precision := 0;

BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	if (nr_seq_conv_receb_p <> 0) then
		begin

		select	coalesce(sum(a.vl_baixa),0)
		into STRICT	vl_baixa_w
		from	movto_banco_pend_baixa a
		where	a.nr_seq_conv_receb  = nr_seq_conv_receb_p;

		select	coalesce(max(a.vl_recebimento),0)
		into STRICT	vl_recebimento_w
		from	convenio_receb a
		where	a.nr_sequencia = nr_seq_conv_receb_p;

		if	((vl_baixa_p + vl_baixa_w) > vl_recebimento_w) then
			begin
			/*r.aise_application_error(-20011,
			obter_texto_tasy(104043, wheb_usuario_pck.get_nr_seq_idioma) || '#@#@');*/
			CALL wheb_mensagem_pck.exibir_mensagem_abort(104043);
			end;
		end if;
		end;
	end if;

	if (nr_adiantamento_p <> 0) then
		begin

		select	coalesce(max(a.vl_adiantamento),0)
		into STRICT	vl_adiantamento_w
		from	adiantamento a
		where	a.nr_adiantamento = nr_adiantamento_p;

		if (vl_baixa_p <> vl_adiantamento_w) then
			begin
			/*r.aise_application_error(-20011,
			obter_texto_tasy(104044, wheb_usuario_pck.get_nr_seq_idioma) || '#@#@');*/
			CALL wheb_mensagem_pck.exibir_mensagem_abort(104044);
			end;
		end if;
		end;
	end if;

	if (nr_seq_cheque_p <> 0) then
		begin

		if (ie_considerar_p IS NOT NULL AND ie_considerar_p::text <> '') then
			begin
			select	coalesce(obter_juro_multa_cheque(max(a.nr_seq_cheque), dt_baixa_p,'R', 'A' ),0)
			into STRICT	vl_multa_w
			from	cheque_cr a
			where	a.nr_seq_cheque = nr_seq_cheque_p;
			end;
		end if;

		select	coalesce(max(a.vl_cheque),0)
		into STRICT	vl_cheque_w
		from	cheque_cr a
		where	a.nr_seq_cheque = nr_seq_cheque_p;

		if	(vl_baixa_p <> (vl_cheque_w + vl_multa_w)) then
			begin
			/*r.aise_application_error(-20011,
			obter_texto_tasy(104045, wheb_usuario_pck.get_nr_seq_idioma) || '#@#@');*/
			CALL wheb_mensagem_pck.exibir_mensagem_abort(104045);
			end;
		end if;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
