CREATE OR REPLACE PROCEDURE mprev_add_grupo_turma_espera ( nr_seq_turma_p bigint, nr_seq_participante_p bigint, ie_motivo_p text, nm_usuario_p text, ie_opcao_p text, ie_urgencia_p bigint, ds_observacao_p text, nr_seq_grupo_p bigint default 0) AS $body$
DECLARE


nr_seq_grupo_w			bigint;
nr_seq_grupo_aprov_w		bigint;
dt_aprovacao_w			timestamp;
nr_seq_funcao_colab_w		mprev_funcao_colaborador.nr_sequencia%type;
nr_seq_ordem_w			mprev_grupo_col_aprovacao.nr_seq_ordem%type;
nr_seq_espera_w			mprev_grupo_turma_espera.nr_sequencia%type;
nr_seq_turma_w          	bigint;
nr_seq_grupo_cursor_w		bigint;


c01 CURSOR FOR
	SELECT	a.nr_seq_funcao_colab,
		a.nr_seq_ordem
	from	mprev_grupo_col_aprovacao a
	where	a.nr_seq_grupo = nr_seq_grupo_cursor_w;


BEGIN

if (nr_seq_turma_p = 0 or coalesce(nr_seq_turma_p::text, '') = '') then
	nr_seq_turma_w := null;
	nr_seq_grupo_w := nr_seq_grupo_p;
	nr_seq_grupo_cursor_w := nr_seq_grupo_p;
else
	nr_seq_turma_w := nr_seq_turma_p;
	nr_seq_grupo_w := null;
end if;

if (coalesce(nr_seq_grupo_cursor_w::text, '') = '') then
	select	nr_seq_grupo_coletivo
	into STRICT	nr_seq_grupo_cursor_w
	from  	mprev_grupo_col_turma
	where 	nr_sequencia = nr_seq_turma_p;
end if;

if (nr_seq_grupo_w <> 0 or nr_seq_turma_w <> 0) then

	select	nextval('mprev_grupo_turma_espera_seq')
	into STRICT	nr_seq_espera_w
	;

	insert into mprev_grupo_turma_espera(nr_sequencia,
			 dt_atualizacao,
			 nm_usuario,
			 nr_seq_turma,
			 nr_seq_grupo,
			 dt_atualizacao_nrec,
			 nm_usuario_nrec,
			 nr_seq_participante,
			 ie_motivo,
			 dt_solicitacao,
			 dt_aprovacao,
			 ie_prioridade,
			 ds_observacao)
		values (	 nr_seq_espera_w,
			 clock_timestamp(),
			 nm_usuario_p,
			 nr_seq_turma_w,
			 nr_seq_grupo_w,
			 clock_timestamp(),
			 nm_usuario_p,
			 nr_seq_participante_p,
			 ie_motivo_p,
			 clock_timestamp(),
			 dt_aprovacao_w,
			 ie_urgencia_p,
			 ds_observacao_p);

	open C01;
	loop
	fetch C01 into
		nr_seq_funcao_colab_w,
		nr_seq_ordem_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert into mprev_grupo_turma_aprov(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_espera,
			nr_seq_funcao_prof,
			nr_ordem)
		values (nextval('mprev_grupo_turma_aprov_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_espera_w,
			nr_seq_funcao_colab_w,
			nr_seq_ordem_w);
		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
