CREATE OR REPLACE PROCEDURE mprev_alterar_sit_capta ( nr_seq_participante_p bigint, ie_nova_situacao_p text, ie_commit_p text, nm_usuario_p text, NR_SEQ_MPREV_CAPTACAO bigint DEFAULT NULL) AS $body$
DECLARE



cd_pessoa_fisica_w	varchar(255);
nr_sequencia_w		bigint;
ie_rejeitar_captacao_w	varchar(10);
							

BEGIN

select 	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from 	mprev_participante
where	nr_sequencia = nr_seq_participante_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	
	select 	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from 	mprev_captacao
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and (coalesce(NR_SEQ_MPREV_CAPTACAO::text, '') = '' AND 1=1 OR nr_Sequencia = NR_SEQ_MPREV_CAPTACAO)
	and		ie_status <> 'C';
	
	ie_rejeitar_captacao_w := obter_param_usuario(10155, 13, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_rejeitar_captacao_w);
	
	update	mprev_captacao
	set		ie_status = ie_nova_situacao_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where 	nr_sequencia = nr_sequencia_w
	and (ie_status <> 'A' or ie_rejeitar_captacao_w = 'S');
			
	if (coalesce(ie_commit_p,'S') = 'S') then

		commit;

	end if;
	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
