CREATE OR REPLACE PROCEDURE mprev_alt_sit_capta ( nr_seq_participante_p bigint, nr_seq_segurado_p bigint, ie_nova_situacao_p text, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(255);
nr_sequencia_w		bigint;


BEGIN

select 	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from 	mprev_participante
where	nr_sequencia = nr_seq_participante_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

	if (coalesce(nr_seq_segurado_p::text, '') = '') then
		update	mprev_captacao
		set	ie_status = ie_nova_situacao_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where 	cd_pessoa_fisica = cd_pessoa_fisica_w
		and	ie_status not in ('C','A','R');
	else
		update	mprev_captacao
		set	ie_status = ie_nova_situacao_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where 	cd_pessoa_fisica = cd_pessoa_fisica_w
		and	mprev_obter_dados_captacao(nr_sequencia, clock_timestamp(), 'BN') = nr_seq_segurado_p
		and	ie_status not in ('C','A','R');
	end if;

	if (coalesce(ie_commit_p,'S') = 'S') then
		commit;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
